$(document).ready(function(){const fullscreenContainer=$('#fullscreen-overlay');let startX=0;const minSwipeDistance=120;function openFullscreen(imageElement){const currentSrc=imageElement.data('fullscreen');const currentPk=imageElement.data('pk');const swipeUrl=imageElement.data('swipe-url');$('#image-container img').attr('src',currentSrc);$('#image-container').attr('hx-get',swipeUrl);$('#image-container img').click(function(e){e.stopPropagation();if(typeof get_photo_detail_url==='function'){const href=get_photo_detail_url(currentPk);console.log("href",href);window.location.href=href;}});fullscreenContainer.css('display','flex').hide().fadeIn(300);};function executeSwipe(side,diff=null){console.log("executeSwipe",side);const container=$('#image-container');const url=container.attr('hx-get');const angle=diff?-diff/15:(side==='prev'?-60:60);const sign=side==='prev'?'-':'';if(url){container.css({'transform':`translateX(${sign}150%)rotate(${angle}deg)`,'opacity':'0'});htmx.ajax('GET',url,{target:'#swipe-container',values:{side:side}});}};$(document).on('touchstart','#image-container',function(e){startX=e.originalEvent.touches[0].pageX;$(this).css('transition','none');});$(document).on('touchmove','#image-container',function(e){const currentX=e.originalEvent.touches[0].pageX;let diff=currentX-startX;$(this).css('transform',`translateX(${diff}px)rotate(${diff/15}deg)`);});$(document).on('touchend','#image-container',function(e){const currentX=e.originalEvent.changedTouches[0].pageX;const diff=currentX-startX;if(diff>minSwipeDistance){console.log("swipe prev",diff);setTimeout(()=>executeSwipe("prev",diff),20);}else if(diff<-minSwipeDistance){console.log("swipe next",diff);setTimeout(()=>executeSwipe("next",diff),20);}else{$(this).css('transform','translateX(0) rotate(0)');};startX=0;});$('.gallery-image').click(function(){openFullscreen($(this));});$('#prev-image').click(function(){console.log("prev-image");setTimeout(()=>executeSwipe("prev"),20);});$('#next-image').click(function(){console.log("next-image");setTimeout(()=>executeSwipe("next"),20);});$('#close-fullscreen').click(function(){fullscreenContainer.fadeOut(300);});fullscreenContainer.find('div[class="image-container"]').click(function(e){if(e.target===e.currentTarget){fullscreenContainer.fadeOut(300);}});const urlParams=new URLSearchParams(window.location.search);if(urlParams.get('openFullscreen')==='true'){openFullscreen($(`.gallery-image[data-fullscreen="${urlParams.get('firstImage')}"]`));}});